$(function() {
  var FADE_TIME = 200; // ms
  var TYPING_TIMER_LENGTH = 400; // ms
  var COLORS = [
    '#e21400', '#91580f', '#f8a700', '#f78b00',
    '#58dc00', '#287b00', '#a8f07a', '#4ae8c4',
    '#3b88eb', '#3824aa', '#a700ff', '#d300e7'
  ];
	var r = false;
	var wincolors = ['teal','red','maroon','yellow','olive','lime','green','cyan','blue','magenta','purple','gray','silver'];

  // Initialize variables
	var $window = $(window);
	var $usernameInput = $('.usernameInput'); // Input for username
	var $messages = $('.messages'); // Messages area
	var $inputMessage = $('.inputMessage'); // Input message input box

	var $loginPage = $('.login.page'); // The login page
	var $chatPage = $('.chat.page'); // The chatroom page

  // Prompt for setting a username
	var username;
	var connected = false;
	var typing = false;
	var lastTypingTime;
	var $currentInput = $usernameInput.focus();
	var socket = io();
	var customcolor = 99;
	var markdown_enabled = true;
	var console_emulation = false;
	var loaded = false;

  function addParticipantsMessage (data) {
    var message = '';
    if (data.numUsers === 1) {
      message += "there's 1 shitposter online";
      $('#online').html('1 user online');
    } else {
      message += "there are " + data.numUsers + " shitposters online";
      $('#online').html(data.numUsers+" users online");
     }
     //clear out the users div
       $('#users').html('');
      //init some shit
	 var output = "";
	 //for each user, add to output
    $.each(data.userlist, function( index, value ) {
	output += "<li>"+value+"</li>";
		});
	
	//output the output to the users div
	$('#users').html(output);
	$('#online').html(data.userlist.length+" users online");
    
    
    log(message);
    //console.log(data);
    //console.log(data.userlist);
    users = data.userlist;
    //get for each of the last messages, add them to the chat
   
    $.each(data.messages, function( index, value ) {
		addChatMessage(value);
	});
	loaded = true;
  }

  // Sets the client's username
  function setUsername () {
    username = cleanInput($usernameInput.val().trim());
	//check for some salty names. 
	var upper = username.toUpperCase();
	if (upper == "NIGGER" || upper == "FAGGOT") username = "BronyLuvr69";
	
    // If the username is valid
    if (username) {
		
      $loginPage.fadeOut();
      $chatPage.show();
      $loginPage.off('click');
      $currentInput = $inputMessage.focus();

      // Tell the server your username
      socket.emit('add user', username);
    }
  }

  // Sends a chat message
  function sendMessage () {
    var message = $inputMessage.val();
    // Prevent markup from being injected into the message
    //message = cleanInput(message);
    // if there is a non-empty message and a socket connection
    if (message && connected) {
      $inputMessage.val('');
      
      if (message == "/help" || message == "help" || message == "/h") 
      {
		  log("this is the help text");
		  log("type '@', a couple letters, and tab to autocomplete");
		  log("click a users name in the chat to mention them");
		  log("type '/color {0-12}' to set your color");
		  log("prepend your message with a backtick for rainbow text");
		  log("Toggle markdown rendering with '/markd'");
		  log('Toggle "baud mode" with /console');
		  log("Discord Link: https://discord.gg/NBgdtPR");
		  
		  return;
		 }
      var time = new Date().toLocaleTimeString('en-US', { hour: 'numeric', hour12: true, minute: 'numeric' });
	//add the visual message
      addChatMessage({
        username: username,
        message: message,
        customcolor: customcolor,
        timestamp: time
      });
      //make the object for the server to use. 
      var data = {
		  username: username,
		  message: message,
		  customcolor: customcolor,
		  timestamp: time
		  };
		  
      // tell server to execute 'new message' and send along one parameter
      socket.emit('new message',data);
    }
  }

  // Log a message
  function log (message, options) {
    var $el = $('<li>').addClass('log').text(message);
    addMessageElement($el, options);
  }

  // Adds the visual chat message to the message list
  function addChatMessage (data, options) {
    // Don't fade the message in if there is an 'X was typing'
	
    //change the title. 
    
    if (data.message.replace(/\s/g,'') == "") return;
	
	if (!document.hasFocus()) $(document).attr("title", data.username+" messaged!");
	else $(document).attr("title", "CH4t");
	
	var $timestampDiv = $("<span class='timestamp'/>").text(data.timestamp).css('color','white');

    var $usernameDiv = $('<span onclick="mention(this)"class="username"/>')
      .text(data.username)
      .css('color', getUsernameColor(data.username));
      
     
	var msg = data.message;

	msg = link(msg);

	
	if (markdown_enabled) msg = marked(msg);
	
	msg = image(msg);
	msg = image2(msg);
	
	if (msg.includes('atob('))return;
	
	//parser for the color command
	
     if (msg.substring(0, 6) == "/color" && data.username == username)
     
     {
		 var cmd = msg.split(" ");
		 var color = cmd[1];
		 if (color > wincolors.length || color < 0 || !Number.isInteger(Number(color))) customcolor = 99;
		 else customcolor = color;
		 
		 log ("color set to "+wincolors[customcolor]);
		 
		 return;
	}
	if (loaded == true)
	{
	if (msg.substring(0,6) == "/markd" && data.username == username)
	{
	markdown_enabled = !markdown_enabled;
	log('markdown toggled');
	return;	
	}
	
	if (msg.substring(0,8) == "/console" && data.username == username)
	{
	console_emulation = !console_emulation;
	log('console emulation toggled');
	return;	
	}
	
	if (msg.substring(0, 6) == "/color" && data.username !== username)
    {
		 return;
	}
	
	}
	if (msg[0]=="`")
	{
		msg = msg.slice(1,msg.length);
		r = true;
	}
	else r = false;
	//console.log(customcolor);
      
    if (data.customcolor !== 99)
    {
		
    var $messageBodyDiv = $('<span class="messageBody">')
      .html(msg)
      .css('color', wincolors[data.customcolor]);
	}
      
    else{    var $messageBodyDiv = $('<span class="messageBody">')
      .html(msg)
      .css('color', wincolors[rand(0,wincolors.length)]);
	}
	
	
	if (r == true) {$messageBodyDiv.rainbowize(); }
	
	 if (msg.includes("@"+username))
     {
		 if (typeof window.orientation == 'undefined' && !document.hasFocus() ){
		 $.notify(msg,{
			 title: data.username +" messaged you",
			 icon:"icon.png"
			 });
		 }
		$messageBodyDiv.css('background-color','white').css('color','black');
	}

    var typingClass = data.typing ? 'typing' : '';
    var $messageDiv = $('<li class="message"/>')
      .data('username', data.username)
      .addClass(typingClass)
      .append($usernameDiv, $messageBodyDiv, $timestampDiv);

    addMessageElement($messageDiv, options);
  }

  // Adds the visual chat typing message
  function addChatTyping (data) {
    data.typing = true;
    data.message = 'is typing';
    addChatMessage(data);
  }

  // Removes the visual chat typing message
  function removeChatTyping (data) {
    getTypingMessages(data).fadeOut(function () {
      $(this).remove();
    });
  }

  // Adds a message element to the messages and scrolls to the bottom
  // el - The element to add as a message
  // options.fade - If the element should fade-in (default = true)
  // options.prepend - If the element should prepend
  //   all other messages (default = false)
  function addMessageElement (el, options) {
    var $el = $(el);

    // Setup default options
    if (!options) {
      options = {};
    }
    if (typeof options.fade === 'undefined') {
      options.fade = true;
    }
    if (typeof options.prepend === 'undefined') {
      options.prepend = false;
    }

    // Apply options
    if (options.fade) {
      $el.hide().fadeIn(FADE_TIME);
    
    }
    if (options.prepend) {
      $messages.prepend($el);
    } else {
      $messages.append($el);
      var msg = $($el.children()[1]).text();
      //console.log(msg);
      if (msg !=="" && !msg.includes('http') && console_emulation == true)
      {
      $($el.children()[1]).text("");
      showText($($el.children()[1]), msg, 0, 100);
      //
		}
      
    }
    $messages[0].scrollTop = $messages[0].scrollHeight;
  }

  // Prevents input from having injected markup
  function cleanInput (input) {
    return $('<div/>').text(input).text();
  }

  // Updates the typing event
  function updateTyping () {
    if (connected) {
      if (!typing) {
        typing = true;
        socket.emit('typing');
      }
      lastTypingTime = (new Date()).getTime();

      setTimeout(function () {
        var typingTimer = (new Date()).getTime();
        var timeDiff = typingTimer - lastTypingTime;
        if (timeDiff >= TYPING_TIMER_LENGTH && typing) {
          socket.emit('stop typing');
          typing = false;
        }
      }, TYPING_TIMER_LENGTH);
    }
  }

  // Gets the 'X is typing' messages of a user
  function getTypingMessages (data) {
    return $('.typing.message').filter(function (i) {
      return $(this).data('username') === data.username;
    });
  }
  
  function rand(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min)) + min;
}

  // Gets the color of a username through our hash function
  function getUsernameColor (username) {
    // Compute hash code
    var hash = 7;
    for (var i = 0; i < username.length; i++) {
       hash = username.charCodeAt(i) + (hash << 5) - hash;
    }
    // Calculate color
    var index = Math.abs(hash % COLORS.length);
    return COLORS[index];
  }

  // Keyboard events

  $window.keydown(function (event) {
    // Auto-focus the current input when a key is typed
    if (!(event.ctrlKey || event.metaKey || event.altKey)) {
      $currentInput.focus();
    }
    // When the client hits ENTER on their keyboard
    if (event.which === 13) {
      if (username) {
        sendMessage();
        socket.emit('stop typing');
        typing = false;
      } else {
        setUsername();
      }
    }
  });

  $inputMessage.on('input', function() {
    updateTyping();
  });

  // Click events

  // Focus input when clicking anywhere on login page
  $loginPage.click(function () {
    $currentInput.focus();
  });

  // Focus input when clicking on the message input's border
  $inputMessage.click(function () {
    $inputMessage.focus();
  });

  // Socket events
	
  // Whenever the server emits 'login', log the login message
  socket.on('login', function (data) {
    connected = true;
   
    // Display the welcome message
    var message = "Welcome to CH4T – be nice or dont...";
    log(message, {
      prepend: true
    });
    log("type '/help' for helptext");
    addParticipantsMessage(data);
  });

  // Whenever the server emits 'new message', update the chat body
  socket.on('new message', function (data) {
    addChatMessage(data);
    //console.log(data);
  });

  // Whenever the server emits 'user joined', log it in the chat body
  socket.on('user joined', function (data) {
	 $('#users').html('');
	 var output = "";
    $.each(data.userlist, function( index, value ) {
	output += "<li>"+value+"</li>";
		});
		userlist = data.userlist;
	$('#users').html(output);
	 $('#online').html(data.numUsers+" users online");
  });

  // Whenever the server emits 'user left', log it in the chat body
  socket.on('user left', function (data) {
    //log(data.username + ' rage quit');
    //addParticipantsMessage(data);
    //removeChatTyping(data);
    	 $('#users').html('');
	 var output = "";
    $.each(data.userlist, function( index, value ) {
	output += "<li>"+value+"</li>";
		});
		userlist = data.userlist;
	$('#users').html(output);
	 $('#online').html(data.numUsers+" users online");
  });

  socket.on('disconnect', function () {
    log('you have been disconnected :(');
  });

  socket.on('reconnect', function () {
    log('you have been reconnected :D');
    if (username) {
      socket.emit('add user', username);
    }
  });

  socket.on('reconnect_error', function () {
    log('attempt to reconnect has failed REEEEE');
  });

});

function changeFavicon(src) {
 var link = document.createElement('link'),
     oldLink = document.getElementById('dynamic-favicon');
 link.id = 'dynamic-favicon';
 link.rel = 'shortcut icon';
 link.href = src;
 if (oldLink) {
  document.head.removeChild(oldLink);
 }
 document.head.appendChild(link);
}

function link(text) {

    var re = /(https?:\/\/(([-\w\.]+)+(:\d+)?(\/([\w/_\.]*(\?\S+)?)?)?))/g;
    return text.replace(re, "<a target='_blank' href=\"$1\" title=\"\">$1</a>");
  }

function bold(text){
    var bold = /\*\*(\S(.*?\S)?)\*\*/gm;
    var html = text.replace(bold, '<strong>$1</strong>');            
    return html;
}

function image(text)
{
	return text.replace(/(http:\/\/[\w\-\.]+\.[a-zA-Z]{2,3}(?:\/\S*)?(?:[\w])+\.(?:jpg|png|gif|jpeg|bmp|svg))/ig, "<img src='$1' alt='' width='400px' height='300px';'>");
}

function image2(text)
{
	return text.replace(/(https:\/\/[\w\-\.]+\.[a-zA-Z]{2,3}(?:\/\S*)?(?:[\w])+\.(?:jpg|png|gif|jpeg|bmp|svg))/ig, "<img src='$1' alt='' width='400px' height='300px';'>");
}

