(function( $ ) {
 
  $.fn.rainbowize = function() {
    return this.each(function() {
      var rainbowtext = '';
      var hue=0;
      var step=0;
 
      // get the current text inside element
      var text = $(this).text();
 
      // hue is 360 degrees
      if (text.length > 0)
        step = 360 / (text.length);
 
      // iterate the whole 360 degrees
      for (var i = 0; i < text.length; i++)
      {
        rainbowtext = rainbowtext + '<span style="color:' + color_from_hue(hue) + '">' + text.charAt(i) + '</span>';
        hue += step;
      }
 
      $(this).html(rainbowtext);
    });
  };
})( jQuery );

function color_from_hue(hue)
{
  var h = hue/60;
  var c = 255;
  var x = (1 - Math.abs(h%2 - 1))*255;
  var color;
 
  var i = Math.floor(h);
  if (i == 0) color = rgb_to_hex(c, x, 0);
  else if (i == 1) color = rgb_to_hex(x, c, 0);
  else if (i == 2) color = rgb_to_hex(0, c, x);
  else if (i == 3) color = rgb_to_hex(0, x, c);
  else if (i == 4) color = rgb_to_hex(x, 0, c);
  else color = rgb_to_hex(c, 0, x);
 
  return color;
}
 
function rgb_to_hex(red, green, blue)
{
  var h = ((red << 16) | (green << 8) | (blue)).toString(16);
  // add the beginning zeros
  while (h.length < 6) h = '0' + h;
  return '#' + h;
}

function mention(e)
{
var name = $(e).text();
var input = $('.inputMessage').val();
input = "@"+ name + " " + input;
$('.inputMessage').val(input).trigger('click');


}

//tab autocompleting

$('.inputMessage').on('keydown', function(e) {
  if(e.which == 9) {
	 
	   e.preventDefault(); 
	   //console.log('tab pressed');
	   var value = $('.inputMessage').val();
	   if (value[0] !== "@") return;
	   value = value.slice(1,value.length);
	   //console.log(value);
		var userlist = [];
		
		//get the users online from the user list, since im lazy. 
		
		$('#users li').each(function(i,value){
			userlist.push(value.innerText);
			});
		//console.log(userlist);
		var re = new RegExp(value,"i");
		var matches = userlist.filter(function (v) {return re.test(v)});
		//console.log(matches);
		$('.inputMessage').val('@'+matches[0]+" ");
		
	   }
});

